import React from "react";
import Card from "../../Components/Card/Card";
import { useGlobalContext } from "../../Components/utils/global.context";
import './Favs.css'
import doctor from '../../Components/Imagenes/doctor.png'

//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Favs = () => {
  const {global} = useGlobalContext()

  return (
    <>
      <h1>Dentists Favs</h1>
      <div className='card-grid'>
        {
          global.favorites.length >0
          ?
          global.favorites.map(favorites => 
          <div>
            <h2>{favorites.name}</h2> 
            <img src={doctor}    alt="Doctor"    width="70%"   />  
            <a href={favorites.website}>{favorites.website}</a>
            <p>Ciudad: {favorites.address.city}</p>

            </div>)
          :
          <h2>Aun no hay favoritos...</h2>
        }
        {/* este componente debe consumir los destacados del localStorage */}
        {/* Deberan renderizar una Card por cada uno de ellos */}
      </div>
    </>
  );
};

export default Favs;
