import {useState} from "react";


const Form = () => {

      const [username, setUsername] = useState("")
      const [email, setEmail] = useState("")
      const [submited, setSubmited] = useState(false)
      const [error, setError] = useState(false)
      const handleEmail = (value) =>{
          setEmail(value)
      }
      const handleUsername = (value) => {
          setUsername(value)
      }

      const onHandleSubmit = (e) =>{
          e.preventDefault()
          if(username.length > 5 && /^\w+([.-]?\w+)@\w+([.-]?\w+)(.\w{2,3})+$/.test(email) && email.includes(".com")  ){
              setSubmited(true)
              setError(false)
          }else{
              setError(true)
          }
      }

      const styles = {
        formContainers:{
          boxShadow: "5px 5px 0px grey",
          width: "500px" ,
          marginTop:"50px",
          padding: "10px",
          borderRadius:"5px",
          backgroundColor:"#D5833A",
          display: "flex",
          flexDirection: "column",
          gap: "15px",



        },
        inputContainers : {
            padding: "5px",
            borderRadius:"5px",


          }

        }  

    return (
      <div>


          <form action="" onSubmit={(e) => onHandleSubmit(e)}>
              <div>
                  <label htmlFor="">Ingrese su nombre</label>
                  <input  style={styles.inputContainers}  type="text"  onChange={(e) => handleUsername(e.target.value)}/>
              </div>
              <div>
                  <label htmlFor="">Ingrese su email</label>
                  <input  style={styles.inputContainers}  type="text" onChange={(e) => handleEmail(e.target.value)}/>
              </div>
              {
                  submited ?  <h2>Gracias {username} te contactaremos cuanto antes via mail</h2> : <input type="submit" value="Enviar"  />
              }
          </form>
          {error && <h2>Por favor verifica la informacion nuevamente</h2>}

      </div>
    )
  }



export default Form;
