import React, {useState, useEffect} from 'react'
import { useParams } from 'react-router-dom'
import axios from 'axios'
import './Detail.css'
import doctor from '../../Components/Imagenes/doctor.png'


//Este componente debera ser estilado como "dark" o "light" dependiendo del theme del Context

const Detail = () => {

  const [dentist, setDentist ] = useState(null)
  const {nameId} = useParams()      
  const URL = "https://jsonplaceholder.typicode.com/users/" + nameId
  useEffect(() => {
          axios.get(URL)
          .then(data => setDentist(data.data))

        }, [] )

  // Consumiendo el parametro dinamico de la URL deberan hacer un fetch a un user en especifico

  return (
    <div>
      <h1>Detail Dentist id </h1>
      {dentist ?
      <div>
        <h2>
          {dentist.name}
        </h2>
        <img 
             src={doctor}
             alt="Doctor"
             width="70%"
             />
             <a href={dentist.website}>{dentist.website}</a>
             {/*<p>tel: {dentist.phone}</p>*/}
             {/*<p>Empresa: {dentist.company.name}</p>*/}
             <p>Ciudad: {dentist.address.city}</p>
             <Link to={"/detail/" + dentist.id}>
              Ver Mas
             </Link>
             {
             isInFavorites(dentist.id)
             ?
             <button className="delete" onClick={() => deleteFromFavorites(dentist.id)}>Delete</button>
             :
             <button  className="addfavorite" onClick={()=> addFavorite(dentist)}>Add Favorite</button>
             }


      </div> :

      <h2>Cargando...</h2>

      }
    {/* aqui deberan renderizar la informacion en detalle de un user en especifico */}
      {/* Deberan mostrar el name - email - phone - website por cada user en especifico */}
    </div>
  )
}

export default Detail
