import React from "react";
import { Link } from "react-router-dom";
import './Card.css'
import { useGlobalContext } from "../utils/global.context";
import doctor from '../Images/doctor.jpg'

const Card = ({dentist} ) => {
  const{addFavorite, isInFavorites, deleteFromFavorites} = useGlobalContext()
 /* const addFav = ({dentist})=>{*/
    // Aqui iria la logica para agregar la Card en el localStorage

  return (
    <div className="card">
      <h2>{dentist.name}</h2>

             <img src={doctor}
             alt="Doctor"
             />
             <a href={dentist.website}>{dentist.website}</a>
             <p>tel: {dentist.phone}</p>
             <p>Empresa: {dentist.company.name}</p>
             <p>Ciudad: {dentist.address.city}</p>
             <Link to={"/detail/" + dentist.id}>
              Ver Mas
             </Link>
             {
             isInFavorites(dentist.id)
             ?
             <button onClick={() => deleteFromFavorites(dentist.id)}>Delete</button>
             :
             <button onClick={()=> addFavorite(dentist)}>Add Favorite</button>
             }


        {/* En cada card deberan mostrar en name - username y el id */}

        {/* No debes olvidar que la Card a su vez servira como Link hacia la pagina de detalle */}

        {/* Ademas deberan integrar la logica para guardar cada Card en el localStorage */}
       {/* <button onClick={addFav} className="favButton">Add fav</button> */}
    </div>
  );
};

export default Card;
