import React from 'react'
import { useContext, createContext, useState } from 'react'

const GlobalContext = createContext()
export const useGlobalContext = () => useContext(GlobalContext)

const GlobalContextProvider = ({children}) => {


  const [global, setGlobal] = useState({favorites:[], theme:""})

    const addFavorite = (user) => {
        setGlobal({...global, favorites:[...global.favorites, user]})
    }
    const isInFavorites = (id) => global.favorites.some(favorite => favorite.id ===id)
    const deleteFromFavorites = (id) => {
      setGlobal ({...global, favorites: global.favorites.filter(favorite => favorite.id !== id)})
    }

  return (
    <GlobalContext.Provider value={{global, addFavorite}}>
        {children}
    </GlobalContext.Provider>
  )
}

export default GlobalContextProvider
